import React, { Component } from "react";
import Jumbotron from "../components/Jumbotron";
import { Col, Row, Container } from "../components/Grid";

import { BrowserRouter as Router, Link } from "react-router-dom";

const button={
    margin: 20,
    color: "white",
    textAlign: "center",
    backgroundColor: "#0cbaba",
    backgroundImage: "linear-gradient(315deg, #0cbaba 0%, #380036 74%",
    display: "flex",
    alignContent: "center"
}

class Home extends Component {

  render() {
    return (
        <Container fluid>
            <Row>
                <Col size="md-6">
                    <Jumbotron>
                        <h1>You Can Do This.</h1>
                    </Jumbotron>
                </Col>
            </Row>
            <Row>
                <Col size="sm-6">
                    <Link to="/add" className="ui button" style={button}>Add an exercise</Link>
                </Col>
                <Col size="sm-6">
                    <Link to="/exercise" className="btn btn-light" style={button}>Start working out</Link>
                </Col>
            </Row>
        </Container>
    )
  }
}

export default Home;
