import React, { Component } from "react";
import Jumbotron from "../components/Jumbotron";
import { Col, Row, Container } from "../components/Grid";
import { BrowserRouter as Router, Link } from "react-router-dom";

const button={
    margin: "100px 20px 20px 20px",
    color: "white",
    textAlign: "center",
    backgroundColor: "#0cbaba",
    backgroundImage: "linear-gradient(315deg, #0cbaba 0%, #380036 74%",
    display: "flex",
    alignContent: "center"
}

const title={
    color: "white",
    textAlign: "center",
    padding: 50
}

const container={
    display: "flex",
    alignContent: "center",
}

class Home extends Component {

  render() {
    return (
        <Container fluid style={container}>
            <Row>
                <Col size="md-6">
                        <h1 style={title}>You Can Do This.</h1>
                </Col>
            </Row>
            <Row>
                <Col size="sm-6">
                    <Link to="/add" className="ui button" style={button}>Add an exercise</Link>
                </Col>
                <Col size="sm-6">
                    <Link to="/exercise" className="ui button" style={button}>Start working out</Link>
                </Col>
            </Row>
        </Container>
    )
  }
}

export default Home;
