import React, { Component } from 'react';
import "./style.css";
import API from "../../utils/API";


class Timer extends Component {

    state = {
        seconds: 10,
        // count: 0
        exercises: []
    }

    loadExercises = () => {
        API.getExercises()
          .then(res => this.setState({ exercises: res.data}))
          .catch(err => console.log(err));
          console.log(this.state.exercises)
        const itemsInArray = this.state.exercises.length
        const random = Math.floor(Math.random() * itemsInArray)
        console.log(random)
        console.log(this.state.exercises[random])
    };

    componentDidMount() {
        this.myInterval = setInterval(() => {
            const { seconds, count } = this.state

            if (seconds > 0) {
                this.setState(({ seconds }) => ({
                    seconds: seconds - 1
                }))
            }

            if (seconds === 0) {
                clearInterval(this.myInterval)
                this.loadExercises()

            }

        }, 1000)

    }



    componentWillUnmount() {
        clearInterval(this.myInterval)
    }

    render() {
        const { seconds } = this.state

        return (
            <div className="app">
                <div className="time">
                    {seconds}s
                </div>
                <div></div>
            </div>
        );
    }
};

export default Timer;
